import { Injectable } from '@angular/core'
import { BehaviorSubject } from 'rxjs'
import { Publication } from 'src/app/models/publication'
import { PublicationRepository } from 'src/app/services/publication/publication-repository'
import { PublicationSqliteData } from 'src/app/services/publication/publication-sqlite-data'
import { ulid, decodeTime } from 'ulidx'

@Injectable( {
  providedIn: 'root'
} )
export class PublicationService {

  repository: PublicationRepository = new PublicationSqliteData()

  constructor() { }

  private _publications: Map<string, Publication>                        = new Map()
  private publicationsSubject: BehaviorSubject<Map<string, Publication>> = new BehaviorSubject( this._publications )
  publications$                                                          = this.publicationsSubject.asObservable()

  async init(): Promise<void> {
    await this.repository.init()
    await this.seedPublications()
  }

  private async seedPublications(): Promise<void> {
    const id1 = '01J9ME26P7HC7NB35VEB07R9SZ'
    const id2 = '01J9ME26P8Z1EZ1JVM7KV3M2HA'
    await this.repository.deletePublication( id1 )
    await this.repository.deletePublication( id2 )
    await this.repository.addPublication( {
      id       : id1,
      title    : 'Mascota perdida',
      description: 'perrito perdido',
      image    : 'data:image/jpeg;base64,)',
      createdAt: new Date(decodeTime( id1 ))
    } )
    await this.repository.addPublication( {
      id       : id2,
      title    : 'Celula encontrada',
      description: 'pertenece a...',
      image    : 'data:image/jpeg;base64,',
      createdAt: new Date(decodeTime( id2 ))
    } )
    await this.getPublications()
    console.log('seedPublications', this._publications)
  }

  private async getPublications(): Promise<void> {
    const publications        = await this.repository.getPublications()
    const orderedPublications = publications.sort(
      ( a, b ) => a.createdAt.getTime() - b.createdAt.getTime() )
    this._publications        = new Map( orderedPublications.map( quote => [ quote.id, quote ] ) )
    this.publicationsSubject.next( this._publications )
  }


  async addPublication( publication: Publication ): Promise<void> {
    await this.repository.addPublication( publication )
    this._publications.set( publication.id, publication )
    this.publicationsSubject.next( this._publications )
  }

  async deletePublication( id: string ): Promise<void> {
    await this.repository.deletePublication( id )
    this._publications.delete( id )
    this.publicationsSubject.next( this._publications )
  }

  async getPublication( id: string ): Promise<Publication | undefined> {
    // return this.repository.getQuote( id )
    return this._publications.get( id )
  }

  async updatePublication( publication: Publication ): Promise<void> {
    await this.repository.updatePublication( publication )
    this._publications.set( publication.id, publication )
    this.publicationsSubject.next( this._publications )
  }

}
